mapper.select:
select room_ID, problem_ID, account_ID, room_start_time, room_running_time, room_level, room_state
from room
where room_level = #{level}
  and room_ID not in (
    select room_ID
    from room_member
    where room.account_ID = #{Aid}
)

select room_ID, problem_ID, account_ID, room_start_time, room_running_time, room_level, room_state
from room
where room_state = #{roomState}
  and room_ID in (
    select room_member.room_ID
    from room_member
    where room.account_ID = #{Aid}
)
