mapper.delete:
delete
from room_member
where room_ID = #{Rid}
  and account_ID = #{Aid}

mapper.select:
select room_ID, account_ID, timecost, percentage
from room_member
where room_ID = #{Rid}
  and account_ID = #{AId}

mapper.update:
update room_member
set timecost   = #{timecost},
    percentage = #{percentage}
where room_ID = #{Rid}
  and account_ID = #{Aid}
